source.sqlReaderQuery:
TRUNCATE TABLE ecbeing_goods_tmp;
BULK INSERT ecbeing_goods_tmp
FROM 'appdata/ftpdata/ecbeing/in/goods/ecbeing_goods.csv'
WITH (
    DATA_SOURCE            = 'MyAzureBlobStorage',
    FORMATFILE_DATA_SOURCE = 'MyAzureBlobStorage',
    FORMATFILE             = 'appdata/format/ecbeing_goods.xml');  
SELECT @@ROWCOUNT as count, CURRENT_TIMESTAMP as updt_dt;

UPDATE tmp_planogram_jan_info SET validation_cd = 2
 WHERE shelf_cd IS NULL 
    OR jan_cd IS NULL
    OR LEN(shelf_cd) > 9
    OR LEN(jan_cd) > 13;
SELECT @@ROWCOUNT as count, CURRENT_TIMESTAMP as updt_dt;

INSERT INTO planogram_jan_info (shelf_cd, jan_cd) 
SELECT shelf_cd, RIGHT('0000000000000' + jan_cd, 13)
  FROM tmp_planogram_jan_info
 WHERE validation_cd = 1;
SELECT @@ROWCOUNT as count, CURRENT_TIMESTAMP as updt_dt;

UPDATE tmp_planogram_shop_info SET validation_cd = 2
 WHERE shelf_cd IS NULL 
    OR store_cd IS NULL
    OR LEN(shelf_cd) > 9
    OR LEN(store_cd) > 5;
SELECT @@ROWCOUNT as count, CURRENT_TIMESTAMP as updt_dt;

INSERT INTO planogram_shop_info (shelf_cd, store_cd) 
SELECT shelf_cd, RIGHT('00000' + store_cd, 5)
  FROM tmp_planogram_shop_info
 WHERE validation_cd = 1;
SELECT @@ROWCOUNT as count, CURRENT_TIMESTAMP as updt_dt;

UPDATE tmp_odbms_brakeset_info SET validation_cd = 3
  FROM tmp_odbms_brakeset_info AS tmp
 WHERE tmp.seq_no IN (
        SELECT seq_no
          FROM (
            SELECT seq_no, ROW_NUMBER() OVER(PARTITION BY child_jan_cd ORDER BY seq_no asc) AS row_no
              FROM tmp_odbms_brakeset_info
             WHERE validation_cd = 1
        ) AS dupe
        WHERE row_no <> 1
);
SELECT @@ROWCOUNT as count, CURRENT_TIMESTAMP as updt_dt;

